mapper.update:
update Email
set readck =1 
where mailnum=#{mailnum}

mapper.select:
select count(*)
from Email
where readck=0 and receivemail =#{receivemail}

mapper.update:
update Email
set delck =1 
where mailnum=#{mailnum}

mapper.select:
select *
from Email
where delck=1 and receivemail =#{receivemail}
